function NetworkDiagram({ state }) {
  // state: "baseline" | "overload" | "activated"
  const configs = {
    baseline: {
      dmn: { r: 52, color: "#FF453A", pulse: "slow", opacity: 1, label: "DMN" },
      sn:  { r: 38, color: "#0A84FF", pulse: false, opacity: 0.5, label: "SN" },
      cen: { r: 36, color: "#30D158", pulse: false, opacity: 0.45, label: "CEN" },
    },
    overload: {
      dmn: { r: 68, color: "#FF453A", pulse: "fast", opacity: 1, label: "DMN" },
      sn:  { r: 30, color: "#0A84FF", pulse: false, opacity: 0.3, label: "SN" },
      cen: { r: 28, color: "#30D158", pulse: false, opacity: 0.25, label: "CEN" },
    },
    activated: {
      dmn: { r: 30, color: "#FF453A", pulse: "faint", opacity: 0.35, label: "DMN" },
      sn:  { r: 60, color: "#0A84FF", pulse: "medium", opacity: 1, label: "SN" },
      cen: { r: 44, color: "#30D158", pulse: "slow", opacity: 0.85, label: "CEN" },
    },
  };
  const c = configs[state];
  const W = 260, H = 180;
  // positions: DMN left, SN top-center, CEN right
  const nodes = [
    { key: "dmn", cx: 70,  cy: 100, ...c.dmn },
    { key: "sn",  cx: 130, cy: 48,  ...c.sn  },
    { key: "cen", cx: 190, cy: 100, ...c.cen },
  ];
  const animMap = { slow: "netPulseSlow", fast: "netPulseFast", medium: "netPulseMed", faint: "netPulseFaint" };

  return (
    <div style={{ display: "flex", justifyContent: "center", margin: "0 auto" }}>
      <svg viewBox={`0 0 ${W} ${H}`} width={W} height={H} style={{ overflow: "visible" }}>
        <defs>
          {["slow","fast","medium","faint"].map(s => (
            <filter key={s} id={`glow-${s}`} x="-50%" y="-50%" width="200%" height="200%">
              <feGaussianBlur in="SourceGraphic" stdDeviation={s === "fast" ? "6" : s === "medium" ? "4" : "2"} result="blur" />
              <feMerge><feMergeNode in="blur"/><feMergeNode in="SourceGraphic"/></feMerge>
            </filter>
          ))}
        </defs>

        {/* Connection lines */}
        {[[0,1],[1,2],[0,2]].map(([a,b], i) => (
          <line key={i}
            x1={nodes[a].cx} y1={nodes[a].cy}
            x2={nodes[b].cx} y2={nodes[b].cy}
            stroke="rgba(0,0,0,0.08)" strokeWidth="1" strokeDasharray="4 4"
          />
        ))}

        {/* Nodes */}
        {nodes.map(n => (
          <g key={n.key}>
            {/* Pulse ring */}
            {n.pulse && (
              <circle cx={n.cx} cy={n.cy} r={n.r + 10}
                fill="none" stroke={n.color} strokeWidth="1.5" opacity="0"
                style={{ animation: `${animMap[n.pulse] || "netPulseSlow"} 2s ease-in-out infinite` }}
              />
            )}
            {/* Main circle */}
            <circle cx={n.cx} cy={n.cy} r={n.r}
              fill={`${n.color}18`}
              stroke={n.color}
              strokeWidth={n.pulse ? "1.5" : "1"}
              opacity={n.opacity}
              style={{
                transition: "all 0.7s ease",
                filter: n.pulse ? `url(#glow-${n.pulse})` : "none",
              }}
            />
            {/* Label */}
            <text x={n.cx} y={n.cy + 1} textAnchor="middle" dominantBaseline="middle"
              fill={n.color} fontSize="11" fontFamily="ui-monospace, monospace"
              fontWeight="700" opacity={n.opacity}
            >{n.label}</text>
          </g>
        ))}
      </svg>
      <style>{`
        @keyframes netPulseSlow {
          0%,100% { opacity:0; transform: scale(1); }
          50% { opacity:0.4; transform: scale(1.08); }
        }
        @keyframes netPulseFast {
          0%,100% { opacity:0; transform: scale(1); }
          40% { opacity:0.7; transform: scale(1.15); }
        }
        @keyframes netPulseMed {
          0%,100% { opacity:0; transform: scale(1); }
          50% { opacity:0.5; transform: scale(1.1); }
        }
        @keyframes netPulseFaint {
          0%,100% { opacity:0; }
          50% { opacity:0.2; }
        }
      `}</style>
    </div>
  );
}

